css:
@page{
    size:A4;
   
}

.print-format td, .print-format th {
    vertical-align: middle !important;
    
}

.page-header {
    padding-bottom: 0 ;
    margin: 13px 0 20px;

}
.sh {
    position: relative;
    top: -50px;
    direction: rtl;
    float: left;
    padding: 1px 45px;
  
}



.Services-table td {
    border: 2px solid #00001178;
    color: #a7a2a2;
  
    
}
.border-head{
    background: #bdbdb8;
    height: 46px;
    padding: 13px 0px;
    text-align: center;
    border: 2px solid #00001178;
    color: black;
    font-weight: 800;
    border-radius: 7px;
    /*margin: -2px 0px 2px -3px;*/
    font-size: 14px;
}


.page {
  
    padding: 0 40px;
    margin-top:-40px;

}

.break-section{
    page-break-inside:avoid;
}

@media print {
.print-format{
    padding:0.0in !important;

}
    thead {
        display: table-header-group;

    }

    tfoot {
        display: table-footer-group;
    }

    .page-header,
    .page-header-space {
        height: 110px;
       
        
    }

    .page-footer,
    .page-footer-space {
        height: 80px;
      
    }

    .page-footer {
        position: fixed;
        bottom:0;
        
      
        
    }

    .page-header {
        position: fixed;
        top: 0;
        margin:  0 ;

       
     
    }

    .Services-table td {
        border: 0.25pt solid #0a0a0a ;
        color: #000;
        margin: 1px;
        
        
    }
    .Services-table{
        margin-top:10px;
    }

    .Aligntext {
        text-align: right !important;
    }
    .print-format td, .print-format th {
       padding: 0px !important;
}
.print-format td{
    padding: 3px !important;
    font-size: 13px;
    font-weight:400;
    color:#000;
}

}
