css:
/*paper size*/
@page{
    size:A4;
   
}
.print-format{
    padding-left:0.3in;
    padding-right:0.3in;
}
.page {
    padding: 0 40px;
    /*margin-top:-40px;*/
}

/*keeptables with break-section class  from spanning multiple pages, providing none are taller than a single page.*/
.break-section{
    page-break-inside:avoid;
}
 /*header style*/
    .page-header,
    .page-header-space {
        height: 110px;
    }
    .page-header {
        position: fixed;
        top: 0;
        margin: 0 ;
    }
 .header-lable {
    position: fixed;
    top: 50px;
    direction: rtl;
  left:50px;
}
.footer-lable{
    direction: rtl;
    position: fixed;
    bottom: 55px;
    right:35px;
    z-index:100000000000000;
}
    .title{
       color:#356fc6;
     font-weight:600 ;
     text-align:center;
     line-height:19px;
   
}
.sub-title{
    margin-top:-10px;
    
} 
/*footer style */
    .page-footer,
    .page-footer-space {
        height: 80px;
      
    }

    .page-footer {
        position: fixed;
        bottom:0;
    }
   
   .Services-table{
        margin-top:10px;
        position: relative;
        margin:auto;
    }
   .Services-table th{
    line-height: 18px;
    border-bottom: 0.25pt solid #000 ;
   }
    .Services-table td {
        border: 0.25pt solid #0a0a0a ;
        color: #000;
        margin: 1px;
    }
    
  /*services table th div style*/
   .border-head{
    background: #bdbdb8;
    padding: 13px 0;
    margin-bottom:3px;
    text-align: center;
    border: 1px solid #000;
    color: black;
    font-weight: 500;
    border-radius: 2px;
    font-size: 14px;
}

    /*align item name and description  to right*/
    .Aligntext {
        text-align: right ;
    }
    
    /*style to all table*/
    table td ,
    table th
    {
     vertical-align: middle;
    }
   
    .print-format td, .print-format th {
       padding: 0 !important;
    }
   .print-format td{
    padding: 5px !important;
    font-size: 13px;
    font-weight:400;
    color:#000;
    }

@media print {
 .header-lable {
    top: 10px;
  left:20px;
}
    /*Services tables style*/
    thead {
        display: table-header-group;

    }

    tfoot {
        display: table-footer-group;
    }
   
   .print-format{
       padding-left:0in;
       padding-right:0in;
   }
 

}
