css:
/*paper size*/
@page{
    size:A4;
    margin-bottom:10px;
}
.page {
    padding:0 40px;
}
/* SET A DISTANCE FOR THE HEADER */
    .page-header,
    .page-header-space{
        height:100px;
    }
/*STYLE HEADER contract letterhead*/    
   .page-header{
       position:fixed;
       top:0;
       margin:0;
   }
/*STYLE DIV CONTRACT TYPE AND NUMBER*/   
   .header-lable {
    position: fixed;
    top: 50px;
    direction: rtl;
    left:50px;
    width:40%;

    }
/*CONTRACT TYPE */
    .title{
     color:#356fc6;
     font-weight:600 ;
     text-align:center;
     line-height:19px;

    }
/*CONTRACT NUMBER */    
    .sub-title{
    margin-top:-15px;
    margin-left:auto;
    margin-right:auto;
    } 
/* SET A DISTANCE FOR THE FOOTER*/      
    .page-footer,
    .page-footer-space{
        height:80px;
    }
/*FOOTER STYLE contract letterhead*/     
   .page-footer{
       position:fixed;
       bottom:0;
  /*left:0;*/
  /*right:0;*/
   }
/*PAGE NUMBER*/    
    .footer-lable{
    direction: rtl;
    position: fixed;
    bottom: 55px;
    right:35px;
    z-index:1;
    font-weight:400 ;
    font-size:12px;
    }
/*first table*/
    .t1{
        margin:auto;
        margin-top:-40px;
    }
    .t1 p{
    text-align:center;
    color:#000;
    font-weight:800 ;
    font-size:18px;
    }

    .t1 th {
     color:#000 ;
     font-weight:600;
     text-align:right;
    }
    .servicestable th {
      text-align:right;
      color:#000 ;
      font-weight:600;

    }
  .servicestable tr:nth-last-child(odd)   { background-color:#eee	; }
  .servicestable tr:nth-last-child(even)    { background-color:#fff; }
  .servicestable th , .servicestable td{
        border:1px solid  #D3D3D3;
    }
   .empty-td{
    background:#fff !important;
    border:0px solid  #D3D3D3 !important;
    }
    .tax-schedule-td{
    border:1px solid  #D3D3D3;
    color:#000;
    text-align:right !important;
    background-color:#fff !important;
    font-weight:400;
    }
@media print {
    .header-lable {
    top: 10px;
    left:0;
    }
   thead {
        display: table-header-group;
    }
    tfoot{
        display:table-footer-group;
    }
}
