css:
@page{
    size:A4;
    margin-bottom:20px;
}
.page {
    padding:0 40px;
}
 .t1{
        margin:auto;
        margin-top:-40px;
    }
    .t1 p{
    text-align:center;
    color:#000;
    font-weight:800 ;
    font-size:18px;
    }

    .t1 th {
     color:#000 ;
     font-weight:600;
     text-align:right;
    }
     .servicestable th {
      text-align:right;
      color:#000 ;
      font-weight:600;

    }
  .servicestable tr:nth-last-child(odd)   { background-color:#eee	; }
  .servicestable tr:nth-last-child(even)    { background-color:#fff; }
  .servicestable th , .servicestable td{
        border:1px solid  #D3D3D3;
    }
   thead {
        display: table-header-group;
    }
    tfoot{
        display:table-footer-group;
    }
    .tax-schedule td{
    border:1px solid  #D3D3D3;
    color:#000;
    font-weight:400;

    }
    .page-header,
    .page-header-space{
        height:100px;
    }
   .page-header{
       position:fixed;
       top:0;
       margin:0;
   }
   .header-lable {
    position: fixed;
    top: 10px;
    direction: rtl;
    left:0;
    width:40%;

    }
    .footer-lable{
    direction: rtl;
    position: fixed;
    bottom: 55px;
    right:35px;
    z-index:1;
     font-weight:400 ;
     font-size:12px;
    }
    .title{
    color:#356fc6;
     font-weight:600 ;
     text-align:center;
     line-height:19px;

    }
    .sub-title{
    margin-top:-15px;
    margin-left:auto;
    margin-right:auto;
    } 
    .page-footer,
    .page-footer-space{
        height:80px;
    }
   .page-footer{
       position:fixed;
       bottom:0;

   }
